"use client";

import { useMemo, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

type Project = {
  title: string;
  description: string;
  tech: string[];
};

const ALL_PROJECTS: Project[] = [
  {
    title: "Retail Sales Performance Dashboard",
    description:
      "Comprehensive dashboard for tracking retail sales performance and optimization opportunities.",
    tech: ["Power BI", "SQL", "Excel"],
  },
  {
    title: "Customer CRM & Monthly Scheme Tracker",
    description:
      "Integrated CRM with automated tracking for onboarding and installment management.",
    tech: ["Looker Studio", "Google Sheets", "SQL"],
  },
  {
    title: "HR & Salary Automation System",
    description:
      "Automated HR reporting system for salary calculations, leave and attendance.",
    tech: ["Excel Macros", "VLOOKUP", "Google Sheets"],
  },
];

const FILTERS = [
  "All",
  "Power BI",
  "SQL",
  "Excel",
  "Looker Studio",
  "Google Sheets",
  "Macros",
];

export default function Projects() {
  const [active, setActive] = useState<string>("All");

  const filtered = useMemo(() => {
    if (active === "All") return ALL_PROJECTS;
    return ALL_PROJECTS.filter((p) =>
      p.tech.some((t) => t.toLowerCase().includes(active.toLowerCase()))
    );
  }, [active]);

  return (
    <div>
      <div className="flex flex-wrap items-center justify-center gap-2 mb-8">
        {FILTERS.map((f) => (
          <button
            key={f}
            onClick={() => setActive(f)}
            className={`rounded-full px-4 py-2 text-sm font-medium border transition-colors ${
              active === f
                ? "bg-blue-600 text-white border-blue-600"
                : "border-foreground/20 text-foreground hover:bg-foreground/10"
            }`}
          >
            {f}
          </button>
        ))}
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        <AnimatePresence mode="popLayout">
          {filtered.map((project) => (
            <motion.div
              key={project.title}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.25 }}
              className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg"
            >
              <h3 className="text-xl font-semibold mb-2 text-blue-600 dark:text-blue-400">
                {project.title}
              </h3>
              <p className="text-gray-600 dark:text-gray-300 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2">
                {project.tech.map((t) => (
                  <span
                    key={t}
                    className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded text-xs"
                  >
                    {t}
                  </span>
                ))}
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
}


