"use client";

import { useEffect, useState } from "react";

export default function ThemeToggle() {
  const [isDark, setIsDark] = useState<boolean>(false);
  const [mounted, setMounted] = useState<boolean>(false);

  useEffect(() => {
    // On mount, read saved preference or system setting
    const saved = typeof window !== "undefined" ? localStorage.getItem("theme") : null;
    const prefersDark = typeof window !== "undefined" ? window.matchMedia("(prefers-color-scheme: dark)").matches : false;
    const enableDark = saved ? saved === "dark" : prefersDark;
    setIsDark(enableDark);
    if (enableDark) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    setMounted(true);
  }, []);

  const toggleTheme = () => {
    const next = !isDark;
    setIsDark(next);
    if (next) {
      document.documentElement.classList.add("dark");
      localStorage.setItem("theme", "dark");
    } else {
      document.documentElement.classList.remove("dark");
      localStorage.setItem("theme", "light");
    }
  };

  if (!mounted) return null;

  return (
    <button
      aria-label="Toggle dark mode"
      onClick={toggleTheme}
      className="inline-flex items-center gap-2 rounded-full border border-foreground/20 px-3 py-1.5 text-sm font-medium text-foreground hover:bg-foreground/10 transition-colors"
    >
      {isDark ? (
        <svg className="h-4 w-4" viewBox="0 0 24 24" fill="currentColor" aria-hidden>
          <path d="M21.64 13.01A9 9 0 1 1 10.99 2.36 7 7 0 0 0 21.64 13z" />
        </svg>
      ) : (
        <svg className="h-4 w-4" viewBox="0 0 24 24" fill="currentColor" aria-hidden>
          <path d="M6.76 4.84l-1.8-1.79L3.17 4.84l1.79 1.79 1.8-1.79zM1 13h3v-2H1v2zm10 10h2v-3h-2v3zm9-10h3v-2h-3v2zM17.24 4.84l1.8-1.79 1.79 1.79-1.79 1.79-1.8-1.79zM12 6a6 6 0 1 0 .001 12.001A6 6 0 0 0 12 6zm6.83 11.36l1.79 1.79-1.79 1.79-1.8-1.79 1.8-1.79zM4.84 17.24l-1.79 1.8 1.79 1.79 1.79-1.79-1.79-1.8z"/>
        </svg>
      )}
      <span className="hidden sm:block">{isDark ? "Dark" : "Light"}</span>
    </button>
  );
}


